mapper.select:
SELECT a.*, category.id AS b_category_id, category.`name` AS b_category_name, l.lookCount AS look_count, c.commentCount AS comment_count, love.loveCount AS love_count FROM biz_article a
INNER JOIN ( select article_id from biz_article_tags where tag_id = #{vo.tagId} group by article_id ) atag on atag.article_id = a.id
INNER JOIN biz_category category ON a.category_id = category.id LEFT JOIN ( SELECT l.article_id, IFNULL(count(1), 0) AS lookCount FROM biz_article_look l GROUP BY l.article_id ) l ON a.id = l.article_id LEFT JOIN ( SELECT sid, IFNULL(count(1), 0) AS commentCount FROM biz_comment c WHERE c.status = 1 GROUP BY sid ) c ON a.id = c.sid LEFT JOIN ( SELECT l.biz_id, IFNULL(count(1), 0) AS loveCount FROM biz_love l WHERE l.biz_type=1 and l.status=1 GROUP BY l.biz_id ) love ON a.id = love.biz_id
AND a.category_id = #{vo.categoryId}
AND a.top = #{vo.top}
AND a.status = #{vo.status}
AND a.recommended = #{vo.recommended}
AND a.slider = #{vo.slider}
AND ( a.title LIKE CONCAT('%',#{vo.keywords , jdbcType=VARCHAR},'%') OR a.content LIKE CONCAT('%',#{vo.keywords , jdbcType=VARCHAR},'%') OR a.description LIKE CONCAT('%',#{vo.keywords , jdbcType=VARCHAR},'%') OR a.keywords LIKE CONCAT('%',#{vo.keywords , jdbcType=VARCHAR},'%') )
ORDER BY RAND()
ORDER BY
a.slider DESC,
a.top DESC,
a.create_time DESC
